import { useState, useRef, useEffect } from "react";
import mapboxgl from "mapbox-gl";
//@ts-ignore
import geojsonURL from "../../data/SP-geojson.json";
import "./styles.css"
import * as turf from "@turf/turf";
// import * as turf from "@turf/turf";

export const Map = () => {
  const mapContainer = useRef<any>(null);

  const hoveredPopup = new mapboxgl.Popup({
    closeButton: false,
    closeOnClick: false,
    className: "floating-popup",
  });


  useEffect(() => {
    mapboxgl.accessToken =
    "pk.eyJ1IjoibGVvc2lsdmFnb21lcyIsImEiOiJja2MwdmxhZjAwejdsMnlsbXFsYTV5ZmVsIn0.MyyvEV2SHjCbCkIUeL_9bA";

    const initializeMap = ({mapContainer }: any) => {
      const map = new mapboxgl.Map({
        container: mapContainer.current,
        style: "mapbox://styles/leosilvagomes/cku2qu4ks2mgz17r100gt7rq6/draft",
        center: [-48.84, -22.5],
        zoom: 6,
      });

      const highlightFeature = (feature : any) => {
        if (feature && feature.geometry) {
          var coordinates = turf.centerOfMass(feature).geometry.coordinates;
          var regionName = feature.properties.SA2_NAME16;
          hoveredPopup
          //@ts-ignore
            .setLngLat(coordinates)
            .setHTML(`<h5>${regionName}</h5>`)
            .addTo(map);

          map.setFeatureState(
            { source: "sa2", id: feature.properties.SA2_MAIN16 },
            { hover: true }
          );
        } else {
          hoveredPopup.remove();
        }
      }

      map.on("load", () => {
        map.dragRotate.disable();
        map.touchZoomRotate.disableRotation();

        map.addSource("sp", {
          type: "geojson",
          //@ts-ignore
          data: geojsonURL,
          promoteId: "SP",
        });


        map.addLayer({
          id: "fill-sp",
          type: "fill",
          source: "sp",
          paint: {
            "fill-color": {
              property: "persons_num",
              stops: [
                [0, "#ADDC91"],
                [5152, "#6CC24A"],
                [12799, "#509E2F"],
                [38695, "#4A7729"],
              ],
            },
            "fill-opacity": 0.8
          },
        });

        map.addLayer({
          id: "sp-borders",
          type: "line",
          source: "sp",
          paint: {
            "line-color": "#ffffff",
            "line-width": 0.8,
            "line-opacity": 1,
          },
        });
      });

      map.on("mousemove", "sp-highlight", (e: any) => {
        if (e.features.length > 0) {
          highlightFeature(e.features[0]);
        }
      });

    };

    if (!map) initializeMap({mapContainer});
  }, [map]);

  return <div ref={(el) => (mapContainer.current = el)} className="map" />;
};
