import { useState, useRef, useEffect } from "react";
import mapboxgl from "mapbox-gl";
//@ts-ignore
import geojsonURL from "../../data/SP-geojson.json";
import "./styles.css"
// import * as turf from "@turf/turf";

export const Map = () => {
  const [map] = useState(null);
  const mapContainer = useRef<any>(null);

  useEffect(() => {
    mapboxgl.accessToken =
      "pk.eyJ1IjoibGVvc2lsdmFnb21lcyIsImEiOiJja2MwdmxhZjAwejdsMnlsbXFsYTV5ZmVsIn0.MyyvEV2SHjCbCkIUeL_9bA";

    const initializeMap = ({mapContainer }: any) => {
      const map = new mapboxgl.Map({
        container: mapContainer.current,
        style: "mapbox://styles/leosilvagomes/cksp5d5ae3qah17o1va6d4yon/draft", // stylesheet location
        center: [-48.84, -22.5],
        zoom: 6,
      });

      console.log(geojsonURL)

      map.on("load", () => {
        map.addSource("sp", {
          type: "geojson",
          //@ts-ignore
          data: geojsonURL,
          promoteId: "SP",
        });

        map.addLayer({
          id: "fill-sp",
          type: "fill",
          source: "sp",
          paint: {
            "fill-color": "#00ffff",
          },
        });

        map.addLayer({
          id: "sp-borders",
          type: "line",
          source: "sp",
          paint: {
            "line-color": "#000000",
            "line-width": 2,
            "line-opacity": 1,
          },
        });
      });

      // map.on('click', (e) => {
      //   map.addSource("sp", {
      //     type: "geojson",
      //     //@ts-ignore
      //     data: geojsonURL,
      //   });
      //   console.log(e.point)
      //   console.log(e.lngLat.wrap());
      // });
    };

    if (!map) initializeMap({mapContainer});
  }, [map]);

  return <div ref={(el) => (mapContainer.current = el)} className="map" />;
};
