import { useState, useRef, useEffect } from "react";
import mapboxgl from "mapbox-gl";
//@ts-ignore
import geojsonURL from "../../data/SP-districts-geojson.json";
import "./styles.css"
// import * as turf from "@turf/turf";
// import * as turf from "@turf/turf";

export const Map = () => {
  const [map, setMap] = useState<mapboxgl.Map>();
  const mapContainer = useRef<any>();

  useEffect(() => {

    mapboxgl.accessToken =  "pk.eyJ1IjoieG16aHUiLCJhIjoiY2tibWlrZjY5MWo3YjJ1bXl4YXd1OGd3bCJ9.xEc_Vf2BkuPkdHhHz521-Q";
    // mapboxgl.accessToken =
      // "pk.eyJ1IjoibGVvc2lsdmFnb21lcyIsImEiOiJja2MwdmxhZjAwejdsMnlsbXFsYTV5ZmVsIn0.MyyvEV2SHjCbCkIUeL_9bA";

    const initializeMap = ({mapContainer}: any) => {
      const map = new mapboxgl.Map({
        container: mapContainer.current,
        style: "mapbox://styles/mapbox/dark-v10",
        // style: "mapbox://styles/leosilvagomes/cku2qu4ks2mgz17r100gt7rq6/draft",
        center: [-46.7,-23.68],
        zoom: 9.2,
      });

      map.resize();

    // new mapboxgl.Popup({ closeButton: false, closeOnClick: false, })
    //   .setLngLat([-46.7,-23.69])
    //   .setHTML('<h4>Hello World!</h4>')
    //   .addTo(map);

      var popup = new mapboxgl.Popup({offset:[-46.7,-23.69]})
        .setText('Construction on the Washington Monument began in 1848.');

      new mapboxgl.Marker({offset:[-46.7,-23.69]})
        .setLngLat([-46.7,-23.69])
        .setPopup(popup)
        .addTo(map);

      // const hoveredPopup = new mapboxgl.Popup({
      //   closeButton: false,
      //   closeOnClick: false,
      //   className: "floating-popup",
      // });

      // const highlightFeature = (feature : any) => {
      //   if (feature && feature.geometry) {
      //     var coordinates = turf.centerOfMass(feature).geometry.coordinates;
      //     var regionName = feature.properties.name;
      //     var regionPersonsNum = feature.properties.persons_num;
      //     hoveredPopup
      //       .setLngLat([coordinates[0], coordinates[1]])
      //       .setHTML(`<h5>${regionName} - ${regionPersonsNum}</h5>`)
      //       .addTo(map);
      //   } else {
      //     hoveredPopup.remove();
      //   }
      // }

      // const clearFeatureHighlight = () => {
      //   highlightFeature(null);
      // }

      console.log(geojsonURL)

      map.on("load", () => {
        map.dragRotate.disable();
        map.touchZoomRotate.disableRotation();

        map.addSource("sp", {
          type: "geojson",
          //@ts-ignore
          data: geojsonURL,
          promoteId: "SP",
        });


        map.addLayer({
          id: "fill-sp",
          type: "fill",
          source: "sp",
          paint: {
            "fill-color":
            {
              property: "PERSONS_NUM",
              stops: [
                [0, "#ADDC91"],
                [55000, "#6CC24A"],
                [110000, "#509E2F"],
                [215000, "#4A7729"],
              ],
            },
            "fill-opacity": 0.8
          },
        });

        map.addLayer({
          id: "sp-borders",
          type: "line",
          source: "sp",
          paint: {
            "line-color": "#ffffff",
            "line-width": 0.8,
            "line-opacity": 1,
          },
        });
      });


      // map.on("mousemove", "fill-sp", (e: any) => {
      //   if (e.features.length > 0) {
      //     highlightFeature(e.features[0]);
      //   }
      // });

      // map.on("mouseleave", "fill-sp", clearFeatureHighlight);

      setMap(map)
    };

    console.log(map)
    if (!map) initializeMap({mapContainer});
  }, [map]);

  return <div ref={(el) => (mapContainer.current = el)} className="map" />;
};
