export const formatPopulationNumber = (value: any) => {
  const valueStr = value.toString();
  const src = valueStr.replace(/[^\d/]/g, "");
  const srcLength = src.length;
  const numbers = src.replace(/[^\d]/g, "");
  const out: any = [];

  for (let i = 0; i < numbers.length; i++) {
    out.push(numbers[i]);
    switch (i) {
      case 1:
        if (i % 3 === 0) {
          out.push(".");
        }
        break;
    }
  }

  console.log(out);
};
