import { Component, createRef } from "react";
import mapboxgl from "mapbox-gl";
//@ts-ignore
import geojsonURL from "../../data/SP-districts-geojson.json";
import "./styles.css"
// import * as turf from "@turf/turf";

let Map = class Map extends Component {
  mapRef = createRef()
  //@ts-ignore
  map
  // hoveredPopup = new mapboxgl.Popup({
  //   closeButton: false,
  //   closeOnClick: false,
  // });

  componentDidMount() {
    this.map = new mapboxgl.Map({
      //@ts-ignore
      container: this.mapRef.current,
      style: "mapbox://styles/leosilvagomes/cku2qu4ks2mgz17r100gt7rq6/draft",
      center: [-46.7,-23.68],
      zoom: 9.2,
    });

    this.map.on("load", () => {
      this.map.dragRotate.disable();
      this.map.touchZoomRotate.disableRotation();

      this.map.addSource("sp", {
        type: "geojson",
        //@ts-ignore
        data: geojsonURL,
        promoteId: "SP",
      });


      this.map.addLayer({
        id: "fill-sp",
        type: "fill",
        source: "sp",
        paint: {
          "fill-color":
          {
            property: "PERSONS_NUM",
            stops: [
              [0, "#ADDC91"],
              [55000, "#6CC24A"],
              [110000, "#509E2F"],
              [215000, "#4A7729"],
            ],
          },
          "fill-opacity": 0.8
        },
      });

      this.map.addLayer({
        id: "sp-borders",
        type: "line",
        source: "sp",
        paint: {
          "line-color": "#ffffff",
          "line-width": 0.8,
          "line-opacity": 1,
        },
      });
    });
  }
}
